CREATE PROC [dbo].[AddMonitorTree]
AS
BEGIN

    --获取监测树ID
    DECLARE @MonitorTreeID INT;
    DECLARE @MonitorTreeType INT;
    DECLARE @MonitorTreeTypeID INT;
    DECLARE @MonitorTreeTypeName NVARCHAR(50);
    DECLARE @AddPropertyTypeID INT;
    DECLARE @AddMonitorTreeID INT;
    DECLARE Device_Cursor CURSOR FOR
    SELECT DISTINCT
        MonitorTreeID
    FROM T_SYS_DEVICE;

    OPEN Device_Cursor;
    FETCH NEXT FROM Device_Cursor
    INTO @MonitorTreeID;
    WHILE @@fetch_status <> -1
    BEGIN

	    PRINT '设备树获取监测树ID';
		PRINT @MonitorTreeID;

        --获取监测树类型及别
        SELECT @MonitorTreeType = TSMT.Type
        FROM dbo.T_SYS_MONITOR_TREE AS TSMT
        WHERE TSMT.MonitorTreeID = @MonitorTreeID;


        --循环监测树类型
        DECLARE MonitorType_cursor CURSOR FOR
        SELECT TDMT.ID,
               TDMT.Name
        FROM dbo.T_DICT_MONITORTREE_TYPE AS TDMT
        WHERE TDMT.ID > @MonitorTreeType;

        OPEN MonitorType_cursor;
        FETCH NEXT FROM MonitorType_cursor
        INTO @MonitorTreeTypeID,
             @MonitorTreeTypeName;
        WHILE @@fetch_status <> -1
        BEGIN

            --插入监测树属性
            INSERT INTO [dbo].[T_SYS_MONITOR_TREE_PROPERTY]
            (
                [Address],
                [URL],
                [TelphoneNO],
                [FaxNO],
                [Latitude],
                [Longtitude],
                [ChildCount],
                [Length],
                [Width],
                [Area],
                [PersonInCharge],
                [PersonInChargeTel],
                [Remark],
                [AddDate]
            )
            SELECT TOP 1
                [Address],
                [URL],
                [TelphoneNO],
                [FaxNO],
                [Latitude],
                [Longtitude],
                [ChildCount],
                [Length],
                [Width],
                [Area],
                [PersonInCharge],
                [PersonInChargeTel],
                [Remark],
                [AddDate]
            FROM [dbo].[T_SYS_MONITOR_TREE_PROPERTY]
            SET @AddPropertyTypeID = SCOPE_IDENTITY();
			PRINT '监测树属性ID';
		    PRINT @AddPropertyTypeID;

            --添加监测树
            INSERT INTO [dbo].[T_SYS_MONITOR_TREE]
            (
                [PID],
                [OID],
                [IsDefault],
                [Name],
                [Des],
                [Type],
                [ImageID],
                [MonitorTreePropertyID],
                [Status],
                [AddDate]
            )
            SELECT @MonitorTreeID,
                   [OID],
                   [IsDefault],
                   [Name]+@MonitorTreeTypeName,
                   [Des],
                   @MonitorTreeTypeID,
                   [ImageID],
                   @AddPropertyTypeID,
                   [Status],
                   [AddDate]
            FROM dbo.T_SYS_MONITOR_TREE AS TSMT
            WHERE TSMT.MonitorTreeID = @MonitorTreeID
            SET @AddMonitorTreeID = SCOPE_IDENTITY();
			PRINT '监测树ID';
			PRINT @AddMonitorTreeID;

            FETCH NEXT FROM MonitorType_cursor
            INTO @MonitorTreeTypeID,
                 @MonitorTreeTypeName;

        END;
        CLOSE MonitorType_cursor;
        DEALLOCATE MonitorType_cursor;

		--修改设备监测树ID
		UPDATE dbo.T_SYS_DEVICE SET MonitorTreeID=@AddMonitorTreeID WHERE MonitorTreeID=@MonitorTreeID

        FETCH NEXT FROM Device_Cursor
        INTO @MonitorTreeID;

    END;
    CLOSE Device_Cursor;
    DEALLOCATE Device_Cursor;

	--添加集团 
	DECLARE @GroupID INT;
	DECLARE @FactoryAddPropertyTypeID INT;
    DECLARE @GroupAddPropertyTypeID INT;

	DECLARE Group_Cursor CURSOR FOR
    SELECT   MonitorTreeID
    FROM dbo.T_SYS_MONITOR_TREE AS TSMT
	WHERE TSMT.Type='1005';

    OPEN Group_Cursor;
    FETCH NEXT FROM Group_Cursor
    INTO @GroupID;
    WHILE @@fetch_status <> -1
    BEGIN

	
            --插入监测树属性
            INSERT INTO [dbo].[T_SYS_MONITOR_TREE_PROPERTY]
            (
                [Address],
                [URL],
                [TelphoneNO],
                [FaxNO],
                [Latitude],
                [Longtitude],
                [ChildCount],
                [Length],
                [Width],
                [Area],
                [PersonInCharge],
                [PersonInChargeTel],
                [Remark],
                [AddDate]
            )
            SELECT TOP 1
                [Address],
                [URL],
                [TelphoneNO],
                [FaxNO],
                [Latitude],
                [Longtitude],
                [ChildCount],
                [Length],
                [Width],
                [Area],
                [PersonInCharge],
                [PersonInChargeTel],
                [Remark],
                [AddDate]
            FROM [dbo].[T_SYS_MONITOR_TREE_PROPERTY]
            SET @FactoryAddPropertyTypeID = SCOPE_IDENTITY();

			     --添加监测树
            INSERT INTO [dbo].[T_SYS_MONITOR_TREE]
            (
                [PID],
                [OID],
                [IsDefault],
                [Name],
                [Des],
                [Type],
                [ImageID],
                [MonitorTreePropertyID],
                [Status],
                [AddDate]
            )
            SELECT 0,
                   0,
                   [IsDefault],
                   [Name]+(SELECT TOP 1 TDMT.Name FROM dbo.T_DICT_MONITORTREE_TYPE AS TDMT WHERE TDMT.ID='1004'),
                   [Des],
                   1004,
                   [ImageID],
                   @FactoryAddPropertyTypeID,
                   [Status],
                   [AddDate]
            FROM dbo.T_SYS_MONITOR_TREE AS TSMT
            WHERE TSMT.MonitorTreeID = @GroupID
            SET @GroupAddPropertyTypeID = SCOPE_IDENTITY();
			PRINT '集团ID';
			PRINT @GroupAddPropertyTypeID;

			--修改工厂父id
			UPDATE dbo.T_SYS_MONITOR_TREE
			SET PID=@GroupAddPropertyTypeID,OID=@GroupAddPropertyTypeID
			WHERE MonitorTreeID=@GroupID

        FETCH NEXT FROM Group_Cursor
        INTO @GroupID;

    END;
    CLOSE Group_Cursor;
    DEALLOCATE Group_Cursor;

END;



