CREATE PROCEDURE [dbo].[SP_UpdateTypeData] (@Type INT)
AS
BEGIN

    --配置表
    IF @Type = 1
    BEGIN
        DECLARE @ConfigDevice_ID INT;
        DECLARE @NewConfigDevice_ID INT;
        DECLARE ConfigDeviceType_Cursor CURSOR FOR
        SELECT ID
        FROM dbo.T_DICT_CONFIG_TEMP
        WHERE ParentId = 8
              AND ID > 57;
        OPEN ConfigDeviceType_Cursor;
        FETCH NEXT FROM ConfigDeviceType_Cursor
        INTO @ConfigDevice_ID;
        WHILE @@fetch_status <> -1
        BEGIN
		
		PRINT '设备类型id'
		PRINT @ConfigDevice_ID

            --添加设备类型
            INSERT INTO [dbo].[T_DICT_CONFIG]
            (
                [Name],
                [IsUsed],
                [Value],
                [IsDefault],
                [AddDate],
                [ParentId],
                [Describe]
            )
            SELECT [Name],
                   [IsUsed],
                   [Value],
                   [IsDefault],
                   [AddDate],
                   [ParentId],
                   [Describe]
            FROM dbo.T_DICT_CONFIG_TEMP
            WHERE ID = @ConfigDevice_ID
            SET @NewConfigDevice_ID = SCOPE_IDENTITY();


			PRINT '新id'
		    PRINT @NewConfigDevice_ID;

            --插入测量位置

            --添加设备类型
            INSERT INTO [dbo].[T_DICT_CONFIG]
            (
                [Name],
                [IsUsed],
                [Value],
                [IsDefault],
                [AddDate],
                [ParentId],
                [Describe]
            )
            SELECT [Name],
                   [IsUsed],
                   [Value],
                   [IsDefault],
                   [AddDate],
                   @NewConfigDevice_ID,
                   [Describe]
            FROM dbo.T_DICT_CONFIG_TEMP
            WHERE ParentId = @ConfigDevice_ID;

            FETCH NEXT FROM ConfigDeviceType_Cursor
            INTO @ConfigDevice_ID;
        END;
        CLOSE ConfigDeviceType_Cursor;
        DEALLOCATE ConfigDeviceType_Cursor;
    END;

    --连接状态表
    IF @Type = 2
    BEGIN
        UPDATE dbo.T_DICT_CONNECT_STATUS_TYPE
        SET OrderNo = 1
        WHERE ID = 1;
        UPDATE dbo.T_DICT_CONNECT_STATUS_TYPE
        SET OrderNo = 2
        WHERE ID = 2;
    END;

    --设备类型
    IF @Type = 3
    BEGIN
        DECLARE @Device_ID INT;
        DECLARE @Device_OrderNo INT;

        SET @Device_OrderNo = 1;
        DECLARE DeviceType_Cursor CURSOR FOR SELECT ID FROM T_DICT_DEVICE_TYPE;
        OPEN DeviceType_Cursor;
        FETCH NEXT FROM DeviceType_Cursor
        INTO @Device_ID;
        WHILE @@fetch_status <> -1
        BEGIN
            --修改设备表结构
            UPDATE dbo.T_DICT_DEVICE_TYPE
            SET OrderNo = @Device_OrderNo
            WHERE ID = @Device_ID;

            SET @Device_OrderNo = @Device_OrderNo + 1;
            FETCH NEXT FROM DeviceType_Cursor
            INTO @Device_ID;
        END;
        CLOSE DeviceType_Cursor;
        DEALLOCATE DeviceType_Cursor;
    END;

    --特征值类型
    IF @Type = 4
    BEGIN
        DECLARE @EigenValue_ID INT;
        DECLARE @EigenValue_OrderNo INT;

        DECLARE EigenValue_Cursor CURSOR FOR
        SELECT @EigenValue_ID,
               @EigenValue_OrderNo
        FROM T_DICT_EIGEN_VALUE_TYPE;
        OPEN EigenValue_Cursor;
        FETCH NEXT FROM EigenValue_Cursor
        INTO @EigenValue_ID,
             @EigenValue_OrderNo;
        WHILE @@fetch_status <> -1
        BEGIN
            --修改配置表结构
            UPDATE dbo.T_DICT_EIGEN_VALUE_TYPE
            SET OrderNo = @EigenValue_OrderNo
            WHERE ID = @EigenValue_ID;
            FETCH NEXT FROM Config_Cursor
            INTO @EigenValue_ID,
                 @EigenValue_OrderNo;
        END;
        CLOSE EigenValue_Cursor;
        DEALLOCATE EigenValue_Cursor;
    END;

    --测量位置监测类型表
    IF @Type = 5
    BEGIN
        UPDATE [dbo].[T_DICT_MEASURE_SITE_MONITOR_TYPE]
        SET OrderNo = 1
        WHERE ID = 1;
        UPDATE [dbo].[T_DICT_MEASURE_SITE_MONITOR_TYPE]
        SET OrderNo = 2
        WHERE ID = 2;
        UPDATE [dbo].[T_DICT_MEASURE_SITE_MONITOR_TYPE]
        SET OrderNo = 3
        WHERE ID = 3;
    END;

    --测量位置类型表
    IF @Type = 6
    BEGIN
        DECLARE @MeasureSiteDeviceType_ID INT;
        DECLARE @MeasureSiteType_OrderNo INT;

        DECLARE @MeasureSiteType_ID INT;
        DECLARE @MeasureSiteType_Index INT;

        DECLARE DeviceType_Cursor CURSOR FOR
        SELECT DISTINCT
            DeviceTypeID
        FROM T_DICT_MEASURE_SITE_TYPE;
        OPEN DeviceType_Cursor;
        FETCH NEXT FROM DeviceType_Cursor
        INTO @MeasureSiteDeviceType_ID;
        WHILE @@fetch_status <> -1
        BEGIN

            PRINT '设备类型';
            PRINT @MeasureSiteDeviceType_ID;

            --修改测量位置类型表
            DECLARE MeasureSiteType_Cursor CURSOR FOR
            SELECT ID
            FROM T_DICT_MEASURE_SITE_TYPE
            WHERE DeviceTypeID = @MeasureSiteDeviceType_ID;
            OPEN MeasureSiteType_Cursor;
            FETCH NEXT FROM MeasureSiteType_Cursor
            INTO @MeasureSiteType_ID;

            SET @MeasureSiteType_Index = 1;
            WHILE @@fetch_status <> -1
            BEGIN

                UPDATE dbo.T_DICT_MEASURE_SITE_TYPE
                SET OrderNo = @MeasureSiteType_Index
                WHERE ID = @MeasureSiteType_ID;

                PRINT '测量位置类型';
                PRINT @MeasureSiteType_ID;

                SET @MeasureSiteType_Index = @MeasureSiteType_Index + 1;

                FETCH NEXT FROM MeasureSiteType_Cursor
                INTO @MeasureSiteType_ID;
            END;
            CLOSE MeasureSiteType_Cursor;
            DEALLOCATE MeasureSiteType_Cursor;

            FETCH NEXT FROM DeviceType_Cursor
            INTO @MeasureSiteDeviceType_ID;
        END;
        CLOSE DeviceType_Cursor;
        DEALLOCATE DeviceType_Cursor;
    END;

    --监测树类型表
    IF @Type = 7
    BEGIN
        UPDATE [dbo].[T_DICT_MONITORTREE_TYPE]
        SET OrderNo = 1
        WHERE ID = 1004;
        UPDATE [dbo].[T_DICT_MONITORTREE_TYPE]
        SET OrderNo = 2
        WHERE ID = 1005;
        UPDATE [dbo].[T_DICT_MONITORTREE_TYPE]
        SET OrderNo = 3
        WHERE ID = 1006;
        UPDATE [dbo].[T_DICT_MONITORTREE_TYPE]
        SET OrderNo = 4
        WHERE ID = 1007;
    END;

    --传感器类型表
    IF @Type = 8
    BEGIN
        UPDATE [dbo].[T_DICT_SENSOR_TYPE]
        SET OrderNo = 1
        WHERE ID = 4;
        UPDATE [dbo].[T_DICT_SENSOR_TYPE]
        SET OrderNo = 2
        WHERE ID = 5;
        UPDATE [dbo].[T_DICT_SENSOR_TYPE]
        SET OrderNo = 3
        WHERE ID = 6;
        UPDATE [dbo].[T_DICT_SENSOR_TYPE]
        SET OrderNo = 4
        WHERE ID = 8;
    END;


    --振动信号类型表
    IF @Type = 9
    BEGIN
        UPDATE [dbo].[T_DICT_VIBRATING_SIGNAL_TYPE]
        SET OrderNo = 1
        WHERE ID = 1;
        UPDATE [dbo].[T_DICT_VIBRATING_SIGNAL_TYPE]
        SET OrderNo = 2
        WHERE ID = 2;
        UPDATE [dbo].[T_DICT_VIBRATING_SIGNAL_TYPE]
        SET OrderNo = 3
        WHERE ID = 3;
        UPDATE [dbo].[T_DICT_VIBRATING_SIGNAL_TYPE]
        SET OrderNo = 4
        WHERE ID = 4;
        UPDATE [dbo].[T_DICT_VIBRATING_SIGNAL_TYPE]
        SET OrderNo = 5
        WHERE ID = 5;
    END;

    --波长类型表
    IF @Type = 10
    BEGIN
